class CurrentWeatherService  attr_reader :latitude, :longitude, :current_weather, :url, :timezone  def initialize(latitude, longitude, current_weather: true)    @url = "https://api.open-meteo.com/v1/forecast"    @latitude = latitude    @longitude = longitude    @current_weather = current_weather    @daily = 'precipitation_probability_max'    @timezone = 'auto'  end  def call    response = Net::HTTP.get_response(uri)    puts JSON.parse(response.body)    @data = JSON.parse(response.body)  end  private  def uri    return @uri if defined?(@uri)    @uri = URI(url)    params = { latitude: latitude, longitude: longitude, current_weather: current_weather, timezone: timezone, daily: @daily }    @uri.query = URI.encode_www_form(params)    @uri  endend